    line-height: 2;
}



.me {
    text-align: center;
    margin-top: 20px;
}

.weixin{
    margin-top: 20px;
    background-color: #e0eaf0;   
    position: relative;
    padding-top: 40px;
    padding-bottom: 40px;
    text-align: center;
}

.weixin img{
    width: 200px;
}

.left-label {
    display: inline-block;
    width: 120px;
}



.container {
    position: relative;
    margin: 0 auto;

    width: 1050px;
    height: 1550px;
    background-color: white;

}

.top {
    position: relative;
    width: 100%;
    height: 290px;
    background-color: #3084bf;
    margin-top: 10px;
    padding-top: 10px;
    padding-left: 20px;
    padding-right: 20px;
    -webkit-border-radius:10px 10px 0 0;
}

.portrait {
    margin: 0 auto;
    position: relative;;
    margin-top: 10px;
    margin-bottom: 10px;
    background-image:url(../image/image.png);
    background-size: 120%;
    background-position:center;
    border-radius:50%;
    background-color: #076f94;
    width:160px;
    height:160px;
}

.side {
    position: absolute;
    width: 360px;
    background-color: #EEEEEE;
    padding: 20px;
}



.main {

    position: absolute;;
    margin-left: 360px;
    min-height: 100%;
    padding: 20px;

}

.info-header:hover {
    background-color: #d4dee4;
}

.info-unit ul li:hover {
    background-color: #d4dee4;
}

.info-header .unit-remove {
    float: right;
    margin-left: 20px;
    visibility: hidden;
    cursor: pointer;

}

.info-header .item-add {
    float: right;
    margin-left: 20px;
    margin-right: 2px;
    visibility: hidden;
    cursor: pointer;

}

 .item-remove {
    position: absolute;
    right: 2px;
    margin-right: 2px;
    visibility: hidden;
    cursor: pointer;

}

ul li{
    position: relative;
}

 .experience-list .item-remove {
    position: absolute;
    right: 2px;
    top: 0px;
    margin-right: 2px;
    visibility: hidden;
    cursor: pointer;

}

 .stack-list .item-remove {
    position: absolute;
    right: 2px;
    top: 0px;
    margin-right: 2px;
    visibility: hidden;
    cursor: pointer;

}

